(()=>{"use strict";class t{static overlay=document.querySelector(".overlay");static addProjectForm=document.querySelector(".add-project-popup");static addProjectButton=document.getElementById("button-add-project");static projectsButton=document.querySelectorAll(".button-default, .button-projects");static addProjectPopup(){t.addProjectForm.reset(),t.overlay.classList.toggle("active"),t.addProjectForm.classList.toggle("active")}static displayProjectTitle(t){document.querySelector("#current-project-title").innerHTML=t.target.innerHTML}static Initialize(){window.addEventListener("keydown",(e=>{t.overlay.classList.contains("active")&&"Escape"===e.key&&t.addProjectPopup()})),t.overlay.addEventListener("click",t.addProjectPopup),t.addProjectButton.addEventListener("click",t.addProjectPopup),t.addProjectForm.addEventListener("submit",(e=>{e.preventDefault(),t.addProjectPopup()})),t.projectsButton.forEach((e=>{e.addEventListener("click",t.displayProjectTitle)}))}}(class{static Initialize(){t.Initialize()}}).Initialize()})();